package com.java;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		System.out.println("Before shift operation: "+a);
		int b=a>>2;
		System.out.println("After right shift operation: "+b);
		int c=a<<1;
		System.out.println("After left shift operation: "+c);
	}

}
