package com.java;

public class Max {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4,b=6,c=9;
		if(a>b && a>c)
		{
			System.out.println(" a is max ");
		}
		else if (b<a && b<c)
		{
			System.out.println("b is max");
		}
		else
		{
			System.out.println(" c is max ");
		}
	}

}
